import { useParams } from "react-router-dom";
import projects from "../data/projects";

const TOKEN_ADDRESS = "0x0c03Ba43E611a6A83A51FF0eB878D8b68c1240C2"; // ví mtk
const ERC20_ABI = [
  "function transfer(address to, uint256 amount) public returns (bool)",
  "function decimals() view returns (uint8)"
];
const HEDERA_PARAMS = {
  chainId: "0x128",
  chainName: "Hedera Testnet",
  nativeCurrency: {
    name: "HBAR",
    symbol: "HBAR",
    decimals: 18
  },
  rpcUrls: ["https://testnet.hashio.io/api"],
  blockExplorerUrls: ["https://hashscan.io/testnet"]
};

function Donate() {
  const { projectId } = useParams();
  const project = projects.find(p => String(p.id) === String(projectId));
  const [walletAddress, setWalletAddress] = useState("");
  const [amount, setAmount] = useState("");
  const [loading, setLoading] = useState(false);
  const [txHash, setTxHash] = useState("");
  const [error, setError] = useState("");

  // Kết nối MetaMask
  const connectWallet = async () => {
    setError("");
    if (!window.ethereum) {
      setError("Vui lòng cài MetaMask!");
      return;
    }
    try {
      const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
      setWalletAddress(accounts[0]);
    } catch (err) {
      setError("Không thể kết nối MetaMask.");
    }
  };

  // Kiểm tra và chuyển mạng Hedera Testnet
  const checkNetwork = async () => {
    const chainId = await window.ethereum.request({ method: "eth_chainId" });
    let chainIdNum = parseInt(chainId, 16);
    if (chainIdNum !== 296) {
      try {
        await window.ethereum.request({
          method: "wallet_switchEthereumChain",
          params: [{ chainId: "0x128" }]
        });
      } catch (switchError) {
        if (switchError.code === 4902) {
          await window.ethereum.request({
            method: "wallet_addEthereumChain",
            params: [HEDERA_PARAMS]
          });
        } else {
          throw switchError;
        }
      }
    }
  };

  // Gửi token MTK
  const handleDonate = async () => {
    setError("");
    setTxHash("");
    if (!walletAddress) {
      setError("Vui lòng kết nối ví MetaMask trước!");
      return;
    }
    if (!amount || isNaN(amount) || Number(amount) <= 0) {
      setError("Vui lòng nhập số lượng MTK hợp lệ.");
      return;
    }
    if (!window.ethereum) {
      setError("Vui lòng cài MetaMask!");
      return;
    }
    if (!project) {
      setError("Không tìm thấy dự án!");
      return;
    }
    try {
      setLoading(true);
      await checkNetwork();
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(TOKEN_ADDRESS, ERC20_ABI, signer);
      const decimals = await contract.decimals();
      const value = ethers.parseUnits(amount, decimals);
      const tx = await contract.transfer(project.wallet, value);
      await tx.wait();
      setTxHash(tx.hash);
    } catch (err) {
      setError(err?.message || "Giao dịch thất bại");
    } finally {
      setLoading(false);
    }
  };

  if (!project) {
    return <div style={{ textAlign: "center", marginTop: 40 }}>Không tìm thấy dự án!</div>;
  }

  return (
    <div style={{ maxWidth: 500, margin: "40px auto", border: "1px solid #eee", borderRadius: 12, padding: 24 }}>
      <h2>{project.name}</h2>
      <p>{project.description}</p>
      <div style={{ marginBottom: 12 }}>
        <b>Ví của bạn:</b>{" "}
        {walletAddress ? (
          <span style={{ color: "green" }}>{walletAddress}</span>
        ) : (
          <button onClick={connectWallet}>Kết nối ví MetaMask</button>
        )}
      </div>
      <div style={{ marginBottom: 12 }}>
        <input
          type="number"
          min="0"
          value={amount}
          onChange={e => setAmount(e.target.value)}
          placeholder="Số lượng MTK"
          style={{ width: "100%", padding: 8, borderRadius: 6, border: "1px solid #ccc" }}
          disabled={loading}
        />
      </div>
      <button
        onClick={handleDonate}
        disabled={loading}
        style={{ width: "100%", padding: 10, borderRadius: 6, border: "none", background: "#1976d2", color: "#fff", cursor: "pointer" }}
      >
        {loading ? "Đang gửi..." : "Quyên góp"}
      </button>
      {txHash && (
        <div style={{ marginTop: 16, color: "green" }}>
          Giao dịch thành công!<br />
          <a
            href={`https://hashscan.io/testnet/transaction/${txHash}`}
            target="_blank"
            rel="noopener noreferrer"
          >
            Xem trên HashScan
          </a>
        </div>
      )}
      {error && <div style={{ color: "red", marginTop: 12 }}>{error}</div>}
    </div>
  );
}

export default Donate;
