import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { ethers } from "ethers";
import projects from "../data/projects";

function DonateDetail() {
  const { id } = useParams();
  const project = projects.find(p => String(p.id) === String(id));
  const [wallet, setWallet] = useState("");
  const [amount, setAmount] = useState("");
  const [loading, setLoading] = useState(false);

  const connectWallet = async () => {
    if (!window.ethereum) {
      alert("Please install MetaMask");
      return;
    }
    try {
      const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
      setWallet(accounts[0]);
    } catch (err) {
      alert("Could not connect MetaMask");
    }
  };

  const handleDonate = async () => {
    if (!wallet) {
      alert("Please connect MetaMask first");
      return;
    }
    if (!amount || isNaN(amount) || Number(amount) <= 0) {
      alert("Please enter a valid amount");
      return;
    }
    setLoading(true);
    try {
      const value = ethers.parseEther(amount.toString()).toString();
      await window.ethereum.request({
        method: "eth_sendTransaction",
        params: [{
          from: wallet,
          to: "0x1111111111111111111111111111111111111111",
          value: value
        }]
      });
      alert("Transaction sent successfully");
    } catch (err) {
      alert(err?.message || "Transaction failed");
    } finally {
      setLoading(false);
    }
  };

  if (!project) return <div style={{textAlign:'center',marginTop:40}}>Project not found</div>;

  return (
    <div style={{ maxWidth: 500, margin: "40px auto", border: "1px solid #eee", borderRadius: 12, padding: 24 }}>
      <h2>{project.name}</h2>
      <p>{project.description}</p>
      <p><b>Target:</b> {project.targetAmount} MTK</p>
      {wallet ? (
        <div style={{ color: "green", marginBottom: 16 }}>Connected: {wallet}</div>
      ) : (
        <button onClick={connectWallet} style={{ padding: "8px 18px", background: "#1976d2", color: "#fff", border: "none", borderRadius: 6, cursor: "pointer", marginBottom: 16 }}>
          Connect MetaMask
        </button>
      )}
      <div style={{ marginBottom: 16 }}>
        <input
          type="number"
          min="0"
          value={amount}
          onChange={e => setAmount(e.target.value)}
          placeholder="Số lượng MTK / HBAR"
          style={{ width: "100%", padding: 8, borderRadius: 6, border: "1px solid #ccc" }}
          disabled={loading}
        />
      </div>
      <button
        onClick={handleDonate}
        disabled={loading || !wallet}
        style={{ width: "100%", padding: 10, borderRadius: 6, border: "none", background: "#1976d2", color: "#fff", cursor: "pointer" }}
      >
        {loading ? "Đang gửi..." : "Gửi tiền quyên góp"}
      </button>
    </div>
  );
}

export default DonateDetail;
