import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { ethers } from "ethers";

function DonateDetail() {
  const { id } = useParams();

  const [wallet, setWallet] = useState("");
  const [amount, setAmount] = useState("");
  const [donorName, setDonorName] = useState("");
  const [projectName, setProjectName] = useState("");
  const [loading, setLoading] = useState(false);

  // Kết nối MetaMask
  const connectWallet = async () => {
    if (!window.ethereum) {
      alert("Vui lòng cài MetaMask");
      return;
    }
    const accounts = await window.ethereum.request({
      method: "eth_requestAccounts",
    });
    setWallet(accounts[0]);
  };

  // Gửi donate
  const handleDonate = async () => {
  if (!wallet) {
    alert("Vui lòng kết nối MetaMask");
    return;
  }

  if (!amount || isNaN(amount) || Number(amount) <= 0) {
    alert("Số tiền không hợp lệ");
    return;
  }

  if (!donorName.trim() || !projectName.trim()) {
    alert("Vui lòng nhập đầy đủ thông tin");
    return;
  }

  setLoading(true);

  try {
    // ĐỊA CHỈ TOKEN MTK (ERC20)
    const TOKEN_ADDRESS = "0x0c03Ba43E611a6A83A51FF0eB878D8b68c1240C2";

    // VÍ DỰ ÁN (NHẬN TIỀN)
    const PROJECT_WALLET = "0x9d5f77169ead4C8C96D63993eD9ef6239092c416";

    const ERC20_ABI = [
      "function transfer(address to, uint256 amount) public returns (bool)",
      "function decimals() view returns (uint8)"
    ];

    const provider = new ethers.BrowserProvider(window.ethereum);
    const signer = await provider.getSigner();
    const token = new ethers.Contract(TOKEN_ADDRESS, ERC20_ABI, signer);

    const decimals = await token.decimals();
    const value = ethers.parseUnits(amount.toString(), decimals);

    // CHUYỂN TOKEN
    const tx = await token.transfer(PROJECT_WALLET, value);
    await tx.wait();

    // LƯU LỊCH SỬ (BACKEND)
    await fetch("http://localhost:5000/api/donations", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      donorName,
      projectId: id,
      projectName,
      amount,
      txHash: tx.hash
    })
    });

    alert("Quyên góp thành công!");
    setAmount("");
    setDonorName("");

  } catch (err) {
    console.error(err);
    alert("Chuyển token thất bại");
  } finally {
    setLoading(false);
  }
};


  return (
    <div style={{ maxWidth: 500, margin: "40px auto" }}>
      <h2>Quyên góp cho dự án</h2>

      <p>
        <b>ID dự án:</b> {id}
      </p>

      {!wallet ? (
        <button onClick={connectWallet}>Kết nối MetaMask</button>
      ) : (
        <p>
          <b>Ví:</b> {wallet}
        </p>
      )}

      <div style={{ marginTop: 16 }}>
        <input
          placeholder="Tên người quyên góp"
          value={donorName}
          onChange={(e) => setDonorName(e.target.value)}
        />
      </div>

      <div style={{ marginTop: 16 }}>
        <input
          placeholder="Tên dự án"
          value={projectName}
          onChange={(e) => setProjectName(e.target.value)}
        />
      </div>

      <div style={{ marginTop: 16 }}>
        <input
          placeholder="Số tiền MTK"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
        />
      </div>

      <div style={{ marginTop: 20 }}>
        <button onClick={handleDonate} disabled={loading}>
          {loading ? "Đang xử lý..." : "Quyên góp"}
        </button>
      </div>
    </div>
  );
}

export default DonateDetail;
