import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { ethers } from "ethers";

function DonateDetail() {
  const { id } = useParams();

  const [wallet, setWallet] = useState("");
  const [amount, setAmount] = useState("");
  const [donorName, setDonorName] = useState("");
  const [projectName, setProjectName] = useState("");
  const [loading, setLoading] = useState(false);

  // Kết nối MetaMask
  const connectWallet = async () => {
    if (!window.ethereum) {
      alert("Vui lòng cài MetaMask");
      return;
    }
    const accounts = await window.ethereum.request({
      method: "eth_requestAccounts",
    });
    setWallet(accounts[0]);
  };

  // Gửi donate
  const handleDonate = async () => {
    if (!wallet) {
      alert("Vui lòng kết nối MetaMask trước");
      return;
    }

    if (!amount || isNaN(amount) || Number(amount) <= 0) {
      alert("Số tiền không hợp lệ");
      return;
    }

    if (!donorName.trim()) {
      alert("Vui lòng nhập tên người quyên góp");
      return;
    }

    if (!projectName.trim()) {
      alert("Vui lòng nhập tên dự án");
      return;
    }

    setLoading(true);

    try {
      const CONTRACT_ADDRESS =
        "0x9d5f77169ead4C8C96D63993eD9ef6239092c416";

      const ABI = ["function donate(uint256 amount) public"];

      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(
        CONTRACT_ADDRESS,
        ABI,
        signer
      );

      const value = ethers.parseUnits(amount.toString(), 18);

      const tx = await contract.donate(value);
      await tx.wait();

      // Lưu lịch sử vào backend
      await fetch("http://localhost:5000/api/donations", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          donorName,
          projectId: id,
          projectName,
          amount,
          txHash: tx.hash,
        }),
      });

      alert("Quyên góp thành công!");
      setAmount("");
      setDonorName("");
    } catch (err) {
      console.error(err);
      alert("Giao dịch thất bại");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: 500, margin: "40px auto" }}>
      <h2>Quyên góp cho dự án</h2>

      <p>
        <b>ID dự án:</b> {id}
      </p>

      {!wallet ? (
        <button onClick={connectWallet}>Kết nối MetaMask</button>
      ) : (
        <p>
          <b>Ví:</b> {wallet}
        </p>
      )}

      <div style={{ marginTop: 16 }}>
        <input
          placeholder="Tên người quyên góp"
          value={donorName}
          onChange={(e) => setDonorName(e.target.value)}
        />
      </div>

      <div style={{ marginTop: 16 }}>
        <input
          placeholder="Tên dự án"
          value={projectName}
          onChange={(e) => setProjectName(e.target.value)}
        />
      </div>

      <div style={{ marginTop: 16 }}>
        <input
          placeholder="Số tiền MTK"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
        />
      </div>

      <div style={{ marginTop: 20 }}>
        <button onClick={handleDonate} disabled={loading}>
          {loading ? "Đang xử lý..." : "Quyên góp"}
        </button>
      </div>
    </div>
  );
}

export default DonateDetail;
