import React, { useEffect, useState } from "react";
import axios from "axios";

function HederaHistory({ accountId }) {
  const [txs, setTxs] = useState([]);

  useEffect(() => {
    axios.get(`https://testnet.mirrornode.hedera.com/api/v1/accounts/${accountId}/transactions`)
      .then(res => setTxs(res.data.transactions))
      .catch(err => console.error(err));
  }, [accountId]);

  return (
    <div>
      <h3>Lịch sử giao dịch Hedera</h3>
      <ul>
        {txs
          .filter(tx => tx.name === "CRYPTOTRANSFER")
          .map(tx => (
            <li key={tx.transaction_id}>
              {tx.transaction_id} - {tx.result}
            </li>
        ))}
      </ul>
    </div>
  );
}

export default HederaHistory;
