import React, { useState } from "react";
import { ethers } from "ethers";

/* ================== C·∫§U H√åNH ================== */

// üëâ Contract token MTK (ERC20)
const TOKEN_ADDRESS = "0x0c03Ba43E611a6A83A51FF0eB878D8b68c1240C2";

// üëâ V√≠ nh·∫≠n ti·ªÅn quy√™n g√≥p (Imported Account 3)
const PROJECT_WALLET = "0x9d5f77169ead4C8C96D63993eD9ef6239092c416";

// üëâ Hedera Testnet
const HEDERA_PARAMS = {
  chainId: "0x128", // 296
  chainName: "Hedera Testnet",
  nativeCurrency: {
    name: "HBAR",
    symbol: "HBAR",
    decimals: 18
  },
  rpcUrls: ["https://testnet.hashio.io/api"],
  blockExplorerUrls: ["https://hashscan.io/testnet"]
};

// üëâ ABI ERC20 t·ªëi thi·ªÉu
const ERC20_ABI = [
  "function transfer(address to, uint256 amount) public returns (bool)",
  "function decimals() view returns (uint8)",
  "function balanceOf(address owner) view returns (uint256)"
];

/* ================== COMPONENT ================== */

function Donate() {
  const [walletAddress, setWalletAddress] = useState("");
  const [amount, setAmount] = useState("");
  const [loading, setLoading] = useState(false);
  const [txHash, setTxHash] = useState("");
  const [error, setError] = useState("");

  /* ===== K·∫æT N·ªêI METAMASK ===== */
  const connectWallet = async () => {
    setError("");
    if (!window.ethereum) {
      setError("Vui l√≤ng c√†i MetaMask");
      return;
    }
    try {
      const accounts = await window.ethereum.request({
        method: "eth_requestAccounts"
      });
      setWalletAddress(accounts[0]);
    } catch {
      setError("Kh√¥ng th·ªÉ k·∫øt n·ªëi MetaMask");
    }
  };

  /* ===== KI·ªÇM TRA / CHUY·ªÇN M·∫†NG ===== */
  const checkNetwork = async () => {
    const chainId = await window.ethereum.request({
      method: "eth_chainId"
    });

    if (parseInt(chainId, 16) !== 296) {
      try {
        await window.ethereum.request({
          method: "wallet_switchEthereumChain",
          params: [{ chainId: "0x128" }]
        });
      } catch (err) {
        if (err.code === 4902) {
          await window.ethereum.request({
            method: "wallet_addEthereumChain",
            params: [HEDERA_PARAMS]
          });
        } else {
          throw err;
        }
      }
    }
  };

  /* ===== G·ª¨I MTK ===== */
  const handleDonate = async () => {
    setError("");
    setTxHash("");

    if (!walletAddress) {
      setError("Vui l√≤ng k·∫øt n·ªëi v√≠ tr∆∞·ªõc");
      return;
    }

    if (!amount || isNaN(amount) || Number(amount) <= 0) {
      setError("Nh·∫≠p s·ªë l∆∞·ª£ng MTK h·ª£p l·ªá");
      return;
    }

    try {
      setLoading(true);
      await checkNetwork();

      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();

      const tokenContract = new ethers.Contract(
        TOKEN_ADDRESS,
        ERC20_ABI,
        signer
      );

      const decimals = await tokenContract.decimals();
      const value = ethers.parseUnits(amount, decimals);

      // ‚úÖ CHUY·ªÇN MTK
      const tx = await tokenContract.transfer(PROJECT_WALLET, value);
      await tx.wait();

      setTxHash(tx.hash);
      setAmount("");
    } catch (err) {
      console.error(err);
      setError(err?.reason || err?.message || "Giao d·ªãch th·∫•t b·∫°i");
    } finally {
      setLoading(false);
    }
  };

  /* ================== UI ================== */
  return (
    <div style={{
      maxWidth: 480,
      margin: "40px auto",
      padding: 24,
      border: "1px solid #ddd",
      borderRadius: 12
    }}>
      <h2>Qu·ªπ Tr·∫ª Em ƒê∆∞·ªùng Ph·ªë</h2>
      <p>H·ªó tr·ª£ tr·∫ª em lang thang t·∫°i TP.HCM</p>

      {!walletAddress ? (
        <button onClick={connectWallet} style={btnStyle}>
          K·∫øt n·ªëi MetaMask
        </button>
      ) : (
        <p style={{ color: "green" }}>
          ƒê√£ k·∫øt n·ªëi: {walletAddress}
        </p>
      )}

      <input
        type="number"
        placeholder="Nh·∫≠p s·ªë l∆∞·ª£ng MTK"
        value={amount}
        onChange={e => setAmount(e.target.value)}
        disabled={loading}
        style={inputStyle}
      />

      <button
        onClick={handleDonate}
        disabled={loading || !walletAddress}
        style={btnStyle}
      >
        {loading ? "ƒêang g·ª≠i..." : "G·ª≠i MTK quy√™n g√≥p"}
      </button>

      {txHash && (
        <p style={{ color: "green", marginTop: 10 }}>
          Th√†nh c√¥ng! TX:
          <br />
          <a
            href={`https://hashscan.io/testnet/transaction/${txHash}`}
            target="_blank"
            rel="noreferrer"
          >
            Xem tr√™n HashScan
          </a>
        </p>
      )}

      {error && (
        <p style={{ color: "red", marginTop: 10 }}>{error}</p>
      )}
    </div>
  );
}

/* ================== STYLE ================== */
const btnStyle = {
  width: "100%",
  padding: 10,
  marginTop: 12,
  background: "#1976d2",
  color: "#fff",
  border: "none",
  borderRadius: 6,
  cursor: "pointer"
};

const inputStyle = {
  width: "100%",
  padding: 8,
  marginTop: 12,
  borderRadius: 6,
  border: "1px solid #ccc"
};

export default Donate;
