	import React from "react";
	import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
	import Donate from "./Donate";
	import DonateDetail from "./pages/DonateDetail";
	// Đã xóa History.jsx
	import theme from "./theme";

	function App() {
		return (
			<Router>
				<div style={{ minHeight: "100vh", background: theme.colors.background }}>
					<nav style={{
						display: "flex",
						justifyContent: "center",
						alignItems: "center",
						gap: 32,
						padding: "24px 0 12px 0",
						background: "#fff",
						boxShadow: "0 2px 8px 0 rgba(25, 118, 210, 0.04)",
						marginBottom: 32
					}}>
						<Link to="/" style={{ fontWeight: 700, textDecoration: "none", color: theme.colors.primary, fontSize: 20 }}>
							Trang chủ
						</Link>
						{/* Đã xóa link Lịch sử giao dịch */}
					</nav>
					<Routes>
						<Route path="/" element={<Donate />} />
						<Route path="/donate/:id" element={<DonateDetail />} />
						{/* Đã xóa route lịch sử giao dịch */}
					</Routes>
				</div>
			</Router>
		);
	}

	export default App;
