	import React from "react";
	import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
	import Donate from "./Donate";
	import DonateDetail from "./pages/DonateDetail";
	import About from "./pages/About.jsx";
	import Contact from "./pages/Contact.jsx";
	// Đã xóa History.jsx
	import theme from "./theme";
	import Footer from "./components/Footer";

	function App() {
		return (
			<Router>
				<div style={{ minHeight: "100vh", background: theme.colors.background, color: theme.colors.text }}>
					<nav style={{
						display: "flex",
						justifyContent: "center",
						alignItems: "center",
						gap: 32,
						padding: "24px 0 12px 0",
						background: "#3f5a83",
						boxShadow: "0 2px 8px 0 rgba(25, 118, 210, 0.04)",
						marginBottom: 32
					}}>
						<Link to="/" style={{ fontWeight: 700, textDecoration: "none", color: "#fff", fontSize: 20 }}>
							Trang chủ
						</Link>
						{/* Đã xóa link Lịch sử giao dịch */}
												<Link to="/about" style={{ fontWeight: 700, textDecoration: "none", color: "#fff", fontSize: 20 }}>
													Về chúng tôi
												</Link>
												<Link to="/contact" style={{ fontWeight: 700, textDecoration: "none", color: "#fff", fontSize: 20 }}>
													Liên hệ
												</Link>
					</nav>
					<Routes>
						<Route path="/" element={<Donate />} />
						<Route path="/donate/:id" element={<DonateDetail />} />
						{/* Đã xóa route lịch sử giao dịch */}
						<Route path="/about" element={<About />} />
						<Route path="/contact" element={<Contact />} />
					</Routes>
				</div>
				<Footer />
			</Router>
		);
	}

	export default App;
