import React from "react";
import theme from "../theme";

function Contact() {
  return (
    <div style={{ ...theme.card, color: theme.colors.text, background: "rgba(255,255,255,0.08)" }}>
      <h2 style={{ color: theme.colors.primary }}>Thông Tin Liên Hệ</h2>
      <p><b>Email:</b> charity.dapp@example.com</p>
      <p><b>Hotline:</b> 0123 456 789</p>
      <p><b>Địa chỉ:</b> 123 Đường Thiện Nguyện, Quận 1, TP. HCM</p>
      <p><b>Facebook:</b> <a href="https://facebook.com/charitydapp" target="_blank" rel="noopener noreferrer">facebook.com/charitydapp</a></p>
    </div>
  );
}

export default Contact;
