// Global theme for Charity Blockchain DApp
const theme = {
  colors: {
    primary: "#1976d2", // blue for trust/blockchain
    accent: "#2e7d32",  // green for charity/community
    background: "#f4f6f8",
    card: "#fff",
    text: "#222",
    border: "#e0e3e7",
    error: "#d32f2f",
    success: "#388e3c"
  },
  card: {
    background: "#fff",
    borderRadius: 14,
    boxShadow: "0 2px 12px 0 rgba(25, 118, 210, 0.06)",
    padding: 28,
    margin: "32px auto",
    maxWidth: 480,
    border: "1px solid #e0e3e7"
  },
  button: {
    background: "#1976d2",
    color: "#fff",
    border: "none",
    borderRadius: 8,
    padding: "10px 24px",
    fontWeight: 600,
    fontSize: 16,
    cursor: "pointer",
    boxShadow: "0 1px 4px 0 rgba(25, 118, 210, 0.10)",
    transition: "background 0.2s"
  },
  buttonAccent: {
    background: "#2e7d32",
    color: "#fff",
    border: "none",
    borderRadius: 8,
    padding: "10px 24px",
    fontWeight: 600,
    fontSize: 16,
    cursor: "pointer",
    boxShadow: "0 1px 4px 0 rgba(46, 125, 50, 0.10)",
    transition: "background 0.2s"
  },
  input: {
    border: "1px solid #e0e3e7",
    borderRadius: 8,
    padding: "10px 12px",
    fontSize: 16,
    width: "100%",
    marginBottom: 18,
    background: "#fff"
  },
  heading: {
    color: "#1976d2",
    fontWeight: 700,
    marginBottom: 12
  },
  success: {
    color: "#388e3c",
    fontWeight: 600
  },
  error: {
    color: "#d32f2f",
    fontWeight: 600
  }
};

export default theme;
