import React, { useState } from "react";

// Thay bằng địa chỉ contract MTK bạn đã deploy
const TOKEN_ADDRESS = "0x0c03Ba43E611a6A83A51FF0eB878D8b68c1240C2";
// Thay bằng ví dự án nhận quyên góp
const PROJECT_WALLET = "0x9d5f77169ead4C8C96D63993eD9ef6239092c416";

function Donate() {
	const [amount, setAmount] = useState(0);
	const [txHash, setTxHash] = useState("");
	const [error, setError] = useState("");
	const [loading, setLoading] = useState(false);

	// Hàm gửi token MTK bằng MetaMask
	const handleDonate = async () => {
		setError("");
		setTxHash("");
		if (!window.ethereum) {
			setError("Vui lòng cài MetaMask!");
			return;
		}
		try {
			setLoading(true);
			// Kết nối MetaMask
			await window.ethereum.request({ method: "eth_requestAccounts" });
			const provider = new window.ethers.providers.Web3Provider(window.ethereum);
			const signer = provider.getSigner();
			// Chuẩn bị contract ERC20
			const abi = [
				"function transfer(address to, uint256 amount) public returns (bool)",
				"function decimals() view returns (uint8)"
			];
			const contract = new window.ethers.Contract(TOKEN_ADDRESS, abi, signer);
			const decimals = await contract.decimals();
			const value = window.ethers.utils.parseUnits(amount.toString(), decimals);
			// Gửi token
			const tx = await contract.transfer(PROJECT_WALLET, value);
			await tx.wait();
			setTxHash(tx.hash);
		} catch (err) {
			setError(err.message || "Giao dịch thất bại");
		} finally {
			setLoading(false);
		}
	};

	return (
		<div style={{ maxWidth: 400, margin: "40px auto", padding: 24, border: "1px solid #eee", borderRadius: 8 }}>
			<h2>Quyên góp MTK</h2>
			<input
				type="number"
				min="0"
				value={amount}
				onChange={e => setAmount(e.target.value)}
				placeholder="Số lượng MTK"
				style={{ width: "100%", marginBottom: 12, padding: 8 }}
			/>
			<button onClick={handleDonate} disabled={loading || !amount} style={{ width: "100%", padding: 10 }}>
				{loading ? "Đang gửi..." : "Quyên góp"}
			</button>
			{txHash && (
				<div style={{ marginTop: 16, color: "green" }}>
					Giao dịch thành công!<br />
					<a href={`https://hashscan.io/testnet/transaction/${txHash}`} target="_blank" rel="noopener noreferrer">Xem trên HashScan</a>
				</div>
			)}
			{error && <div style={{ color: "red", marginTop: 12 }}>{error}</div>}
		</div>
	);
}

export default Donate;
