import React from "react";
import projects from "./data/projects";

function Donate() {
  return (
    <div style={{ maxWidth: 600, margin: "40px auto" }}>
      <h2 style={{ textAlign: "center", marginBottom: 32 }}>Danh sách dự án từ thiện</h2>
      {projects.map(project => (
        <div key={project.id} style={{ border: "1px solid #ddd", borderRadius: 8, padding: 20, marginBottom: 20, background: "#fafbfc" }}>
          <h3>{project.name}</h3>
          <p>{project.description}</p>
          <p><b>Mục tiêu:</b> {project.targetAmount} MTK</p>
          <button style={{ padding: "8px 18px", background: "#1976d2", color: "#fff", border: "none", borderRadius: 6, cursor: "pointer" }}>
            Quyên góp
          </button>
        </div>
      ))}
    </div>
  );
}

export default Donate;
